def recup_vecteur_colonne(mat,numero): 
    #renvoie le vecteur de la colonne (allant de 1 a 25) 'numero' en liste
    vect = []
    for ligne in range(0,25):
        vect = vect + [mat[ligne,numero-1]]
    return vect

def perpendiculaire(a,b):
    somme=0
    if len(a)!=len(b):
        return False
    else:
        for i in range(len(a)):
            somme= plus(somme,mult(a[i],b[i]))
        if somme !=0:
            return False
        else:
            return True

